mapper.update:

update member_level
set serialnumber = #{serialnumber,jdbcType=VARCHAR},
  pserialnumber = #{pserialnumber,jdbcType=VARCHAR},
  bord = #{bord,jdbcType=TINYINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:

select id, serialnumber, pserialnumber, bord
from member_level
where id = #{id,jdbcType=BIGINT}
