select id, login_name, password, status, if_manager, member_id, create_time
from sys_user


    id=#{id}


    and login_name=#{loginName}


    and password=#{password}


    and status=#{status}


    and if_manager=#{ifManager}


    and member_id=#{memberId}


    and create_time=#{createTime}
