mapper.select:
SELECT m.id,m.name,m.sex,m.serialnumber,m.idcard,m.telphone,m.cardno,m.createtime,m.wechat,m.qq,m.status,m.alipay,m.pid,m.group_id,p.name as pName from three_member m
LEFT JOIN three_member p on p.id=m.pid
where m.group_id=#{groupId}

SELECT count(m.id)
from three_member m
LEFT JOIN three_group g on g.id=m.group_id


    m.name=#{name}


    and m.serialnumber=#{serialnumber}


    and m.group_id=#{groupId}

SELECT m.id,m.name,m.sex,m.serialnumber,m.idcard,m.telphone,m.cardno,m.createtime,m.wechat,m.qq,m.status,m.alipay,m.pid,m.group_id,p.name as pName,g.group_name as groupName
from three_member m
LEFT JOIN three_member p on p.id=m.pid
LEFT JOIN three_group g on g.id=m.group_id


    m.name=#{name}


    and m.serialnumber=#{serialnumber}


    and m.group_id=#{groupId}


order by m.create_time asc  limit #{start} , #{limit}
