mapper.insert:

insert into three_reward (id, mid, money, 
  createtime, type, rewardtime
  )
values (#{id,jdbcType=BIGINT}, #{mid,jdbcType=BIGINT}, #{money,jdbcType=INTEGER}, 
  #{createtime,jdbcType=TIMESTAMP}, #{type,jdbcType=TINYINT}, #{rewardtime,jdbcType=DATE}
  )

mapper.select:

select id, mid, money, createtime, type, rewardtime
from three_reward
where id = #{id,jdbcType=BIGINT}

select count(id)
  from(
      SELECT  id
      from three_reward
      where mid=#{mid}
      GROUP BY rewardtime
      ) as tmpTable

